import React, {useState, useEffect} from "react";
import VendorBox from "./VendorBox";
import VendorForm from "./VendorForm"
import styled from "styled-components";
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import Snackbar from '@material-ui/core/Snackbar';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Button from '@material-ui/core/Button';

const HomeContainer = styled.div`
	height: 100%;
	display: flex;
	align-items: center;
	flex-direction: column;
`
const Header = styled.h1`
	margin-top: 9rem;
	font-size: 6rem;
	color: #404040;
	font-weight: 400;
`
const VendorContainer = styled.div`
	margin-top: 12rem;
`
const MuiModal = styled(Modal)`
	display: flex;
	align-items: center;
	justify-content: center;
`
const MuiButton = styled(Button)`
	margin-top: 2rem;
`
export default function Home() {
    const [open, setOpen] = useState(false);
	const [tasksQueued, setTasksQueued] = useState(false)

	useEffect(() => {
		(async function getTasks() {
			const tasks = await fetch('http://localhost:3000/api/tasks');
			const tasksJson = await tasks.json();
			setTasksQueued(tasksJson.length > 0);
		})();
	}, [])


	const handleOpen = () => {
		setOpen(true);
	}

	const handleClose = () => {
		setOpen(false);
	}

	const stopTasks = () => {

	}

    // const classes = useStyles();
    return (
        <HomeContainer>
            <Header>Jay's PS5 Bot ðŸ¤–</Header>
			{tasksQueued && (
            	<MuiButton
            	//   disabled={activeStep === 0}
					variant="contained"
            		onClick={stopTasks}
					color="primary" 
            		size="large"
            	>
            	  Stop all current tasks
            	</MuiButton>
			)}
            <VendorContainer>
                <VendorBox handleOpen={handleOpen}/>
            </VendorContainer>
            <MuiModal
        		aria-labelledby="transition-modal-title"
        		aria-describedby="transition-modal-description"
        		// className={classes.modal}
        		open={open}
        		onClose={handleClose}
        		closeAfterTransition
        		BackdropComponent={Backdrop}
        		BackdropProps={{
        		  timeout: 500,
        		}}
      		>
        		<Fade in={open}>
					<div>
						<VendorForm handleClose={handleClose}/>
					</div>
        		</Fade>
      		</MuiModal>
			  <ToastContainer />
        </HomeContainer>
    )
}
